(function($) {
    "use strict";

    // Stop divi header from shrinking.
    // https://divibooster.com/stop-the-divi-header-from-shrinking-on-scroll/
    // Override the addClass to prevent fixed header class from being added
    var addclass = jQuery.fn.addClass;
    jQuery.fn.addClass = function(){
        var result = addclass.apply(this, arguments);
        jQuery('#main-header').removeClass('et-fixed-header');
        return result;
    }

    $('#main-header').removeClass('et-fixed-header');

})(jQuery);

(function($) {
    function initMobileMenu() {
        var $mainArea = $('#et-main-area');
        var $menuButton = $('.mobile_menu_bar_toggle');

        $menuButton.on('click', function(event) {
            event.preventDefault();

            if ($(event.currentTarget).hasClass('opened')) {
                animateLogo();
                $mainArea.show();
                $(event.currentTarget).removeClass('opened');
            } else {
                animateLogo();
                $mainArea.hide();
                $(event.currentTarget).addClass('opened');
            }
        });
    }

    function animateLogo() {
        var $logo = $('.logo_container');

        $logo.animate({
            top: '-50px'
        }, 'fast', function() {
            window.setTimeout(function() {
                $logo.animate({
                    top: '0px'
                }, 'fast');
            }, 500);
        });
    }

    $(window).load(function() {
        initMobileMenu();
    });
})(jQuery);
